import React from "react";
import styled from "styled-components";

const AppFilter = () => {
  return (
    <>
      <SelectWrapper className="w-100 ps-2 py-2" name="Continents">
        <option value="" disabled hidden selected>
          select a region
        </option>

        <option>Africa</option>

        <option className="">Europe</option>
      </SelectWrapper>
    </>
  );
};

export default AppFilter;

const SelectWrapper = styled.select`
  background-color: var(--darkModeTextAndLightModeElements);
  border: none;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
  &:focus {
    outline: none;
  }
`;
