import React from "react";
import styled from "styled-components";

const AppFilter = () => {
  return (
    <SelectWrapper className="col-4 mt-5">
      <div className="select-parent position-relative">
        <select className="filter-select w-100 ps-2">
          <option className="" data-cont="africa">
            Africa
          </option>
        </select>
      </div>
    </SelectWrapper>
  );
};

export default AppFilter;

const SelectWrapper = styled.div`
  .filter-select:focus {
    outline: none;
  }
  .filter-select {
    appearance: none;
    z-index: 1;
  }
  .select-parent::after {
    content: "";
    width: 0;
    height: 0;
    position: absolute;
    top: calc(50% + 4px);
    right: 5px;
    transform: translateY(-50%);
    border: 8px solid;
    border-color: red transparent transparent transparent;
  }
`;
