import React, { useContext } from "react";
import styled from "styled-components";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMagnifyingGlass } from "@fortawesome/free-solid-svg-icons";
import { Row } from "react-bootstrap";
import { CountriesContext } from "../context/CountriesContext";

const AppSearchField = () => {
  const { darkMode } = useContext(CountriesContext);
  return (
    <SearchFieldWrapper darkmode={darkMode} className="py-2 ps-4">
      <Row>
        <div className="col-2 col-md-1">
          <FontAwesomeIcon icon={faMagnifyingGlass} className="text-muted" />
        </div>
        <div className="col-10">
          <input
          darkmode={darkMode}
            type="text"
            id="search-input"
            className="w-100 pe-3"
            placeholder="Search for a country"
          />
        </div>
      </Row>
    </SearchFieldWrapper>
  );
};

export default AppSearchField;

const SearchFieldWrapper = styled.div`
  box-shadow: 0 0 0.5rem rgba(0, 0, 0, 0.1);
  &,
  #search-input {
    background-color: ${(props) =>
      props.darkmode == true
        ? "var(--darkModeElements)"
        : "var(--lightModeBackground)"};
  }
  #search-input {
    color: var(----lightModeInput);
  }
  #search-input::placeholder {
    font-size: 0.9rem;
  }
`;
