import React, { useContext } from "react";
import styled from "styled-components";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMagnifyingGlass } from "@fortawesome/free-solid-svg-icons";
import { Row } from "react-bootstrap";
import { CountriesContext } from "../context/CountriesContext";

const AppSearchField = () => {
  const { darkMode } = useContext(CountriesContext);
  return (
    <SearchFieldWrapper
      darkMode={darkMode}
      className="py-2 ps-4 d-flex align-items-center gap-4"
    >
      <FontAwesomeIcon
        icon={faMagnifyingGlass}
        className="text-muted search-icon"
        darkMode={darkMode}
      />
      <input
        type="text"
        id="search-input"
        className="w-100 pe-3"
        placeholder="Search for a country"
      />
    </SearchFieldWrapper>
  );
};

export default AppSearchField;

const SearchFieldWrapper = styled.div`
  box-shadow: 0 0 0.5rem rgba(0, 0, 0, 0.1);

  background-color: ${(props) =>
    props.darkMode ? "var(--darkModeElements)" : "var(--lightModeBackground)"};

  .search-icon,{
    color: ${(props) =>
      props.darkMode
        ? "var(--darkModeTextAndLightModeElements)"
        : "var(--lightModeText)"};
  }
  #search-input {
    color: ${(props) =>
      props.darkMode
        ? "var(--darkModeTextAndLightModeElements)"
        : "var(--lightModeText)"};

    background-color: inherit;
  }
  #search-input::placeholder {
    color: ${(props) =>
      props.darkMode
        ? "var(--darkModeTextAndLightModeElements)"
        : "var(--lightModeText)"};
    font-size: 0.9rem;
  }
`;
