import React, { useContext } from "react";
import { CountriesContext } from "../context/CountriesContext";
import CountryItem from "./CountryItem";
import { Row } from "react-bootstrap";

const CountriesList = () => {
  const { countries, status, region, searchedCountry } =
    useContext(CountriesContext);

  let filteredCountries = countries.slice(0,10).filter((country,index));
  if (region !== "All") {
    filteredCountries = filteredCountries
      .slice(0, 8)
      .filter((country) => country.region === region);
  }

  if (searchedCountry !== "") {
    filteredCountries = filteredCountries.filter(
      (country) =>
        country.name.common.toLowerCase() === searchedCountry.toLowerCase()
    );
  }

  const renderedCountries = filteredCountries
    .slice(0, 8)
    .map((country) => <CountryItem key={country.area} country={country} />);

  console.log(renderedCountries);
  return (
    <main className="mt-5">
      <Row className="">{status === "idle" ? renderedCountries : "wait"}</Row>
    </main>
  );
};

export default CountriesList;
