import React, { useContext } from "react";
import { CountriesContext } from "../context/CountriesContext";
import CountryItem from "./CountryItem";

const CountriesList = () => {
  const { countries, status } = useContext(CountriesContext);

  if (status === "idle") {
    const renderedCountries = countries.map((country) => (
      <CountryItem key={country.area} country={country} />
    ));
  }

  return <main className="mt-5">{status==="idle" ?renderedCountries: wait};</main>;
};

export default CountriesList;
