import React, { useContext } from "react";
import { Container, Row } from "react-bootstrap";
import styled from "styled-components";
import { CountriesContext } from "../context/CountriesContext";

const DetailedCountry = () => {
  const { detailedCountry } = useContext(CountriesContext);
  const nativeNameArr = Object.values(detailedCountry.name.nativeName);
  console.log(nativeNameArr);
  const nativeName = nativeNameArr[0].common;
  return (
    <DetailsWrapper className="">
      <Container>
        <Row>
          <div className="col-lg-4">
            <img
              className="flag-img w-100"
              src={detailedCountry.flags.png}
              alt=""
            />
          </div>
          <div className="col-7 ms-auto">
            <main className="info">
              <h4 className="country-name">{detailedCountry.name.common}</h4>
              <Row>
              <div cla></div>
                <h5 className="country-native-name mt-4">
                  Native Name : <span>{nativeName}</span>
                </h5>
                <h5 className="country-population mt-4">
                  Population : <span>{detailedCountry.population}</span>
                </h5>
                <h5 className="country-region mt-4">
                  Region : <span>{detailedCountry.region}</span>
                </h5>
                <h5 className="country-sub-region mt-4">
                  Sub Region : <span>{detailedCountry.subregion}</span>
                </h5>
                <h5 className="country-sub-region mt-4">
                  Capital : <span>{detailedCountry.capital}</span>
                </h5>
              </Row>
            </main>
          </div>
        </Row>
      </Container>
    </DetailsWrapper>
  );
};

export default DetailedCountry;

const DetailsWrapper = styled.section`
  margin-top: 12rem;
`;
