import { faLungs } from "@fortawesome/free-solid-svg-icons";
import React, { useContext } from "react";
import styled from "styled-components";
import { CountriesContext } from "../context/CountriesContext";

const CountryItem = ({ country }) => {
  const { darkMode } = useContext(CountriesContext);

  return (
    <div darkmode={darkMode} className="col-lg-3 g-4 ">
      <BoxWrapper className="box w-100">
        <img className="flag-img w-100" src={country.flags.png} alt="" />
        <div className="text mt-4 ps-4 pb-4">
          <h3 className="name text-capitalize mt-3">egypt</h3>

          <h4 className="pop">
            Population: <span className="pop-num">100000</span>
          </h4>

          <h4 className="reg">
            Region: <span className="reg-name">Africa</span>
          </h4>

          <h4 className="cap">
            Capital: <span className="cap-name">Cairo</span>
          </h4>
        </div>
      </BoxWrapper>
    </div>
  );
};

export default CountryItem;

const BoxWrapper = styled.div`
  background-color: ${(props) =>
    props.darkmode === "dark"
      ? "var(--darkModeElements)"
      : "var(--lightModeBackground)"};
  .text {
    background-color: inherit;
  }
  .flag-img {
    height: 12rem;
  }
  h3,
  h4 {
    color: ${(props) =>
      props.darkmode === "dark"
        ? "var(--darkModeTextAndLightModeElements)"
        : "var(--lightModeText)"};
  }

  span {
  }
`;
