import React, { useContext } from "react";
import { Container, Row } from "react-bootstrap";
import styled from "styled-components";
import { CountriesContext } from "../context/CountriesContext";

const DetailedCountry = () => {
  const { detailedCountry } = useContext(CountriesContext);
  console.log(detailedCountry);
  return (
    <DetailsWrapper className="">
      <Container>
        <Row>
          <div className="col-lg-4">
            <img
              className="flag-img w-100"
              src={detailedCountry.flags.png}
              alt=""
            />
          </div>
          <div className="col-7 ms-auto">
            <main className="info">
              <h4 className="country-name">{detailedCountry.name.common}</h4>
              <h5 className="country-native-name mt-4">
                Native Name : <span>{detailedCountry.name.native}</span>
              </h5>
            </main>
          </div>
        </Row>
      </Container>
    </DetailsWrapper>
  );
};

export default DetailedCountry;

const DetailsWrapper = styled.section`
  margin-top: 12rem;
`;
